import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, TextInput, Image, ScrollView } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const MainPage = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Image source={require('./your-logo.png')} style={styles.logo} />
      </View>
      <ScrollView style={styles.cardContainer}>
        {/* Example card 1 */}
        <View style={styles.card}>
          <Image source={require('./example-image-1.jpg')} style={styles.cardImage} />
          <Text style={styles.cardText}>Lost: Keys</Text>
        </View>

        {/* Example card 2 */}
        <View style={styles.card}>
          <Image source={require('./example-image-2.jpg')} style={styles.cardImage} />
          <Text style={styles.cardText}>Found: Wallet</Text>
        </View>

        {/* Add more cards as needed */}
      </ScrollView>
      <View style={styles.footer}>
        <TextInput
          style={styles.searchBar}
          placeholder="Search..."
          // Add your search functionality here
        />
        <TouchableOpacity style={styles.addButton}>
          <Text style={styles.addButtonText}>+</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFF', // Set your background color
  },
  header: {
    backgroundColor: '#342F2F', // Set your header background color
    alignItems: 'center',
    justifyContent: 'center',
    height: 80,
  },
  logo: {
    width: 120, // Set your logo width
    height: 40, // Set your logo height
    resizeMode: 'contain',
  },
  cardContainer: {
    flex: 1,
    padding: 16,
  },
  card: {
    backgroundColor: '#FFF',
    borderRadius: 12,
    marginBottom: 16,
    padding: 16,
    flexDirection: 'row',
    alignItems: 'center',
  },
  cardImage: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginRight: 16,
  },
  cardText: {
    fontSize: 16,
  },
  footer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
    borderTopWidth: 1,
    borderTopColor: '#CCC', // Set your border color
  },
  searchBar: {
    flex: 1,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 12,
    padding: 8,
    marginRight: 8,
  },
  addButton: {
    backgroundColor: '#342F2F', // Set your button background color
    width: 40,
    height: 40,
    borderRadius: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  addButtonText: {
    color: '#FFF', // Set your button text color
    fontSize: 24,
    fontWeight: 'bold',
  },
});

export default MainPage;
